    public static void getData(){
        StringBuilder stringBuilder=new StringBuilder();
        try {
            //定义文件
            File file =new File("C:\\Users\\Administrator\\Desktop\\1\\new4401000000000.txt");
            //字节读入流，读取文件
            FileInputStream fis = new FileInputStream(file);
            //创建字节数组，提高效率
            byte[] b = new byte[1024];
            int l = 0;
            while ((l = fis.read(b)) != -1) {
                //使用字符串构造方法
                //第一个参数为要连接元素的数组 第二个参数为起始下标 第三个参数为连接长度
                stringBuilder.append(new String(b, 0, l));
            }
            //注意关闭流，这样IO流才能从内存写到磁盘，不关不会执行io操作
            String sqltxt=stringBuilder.toString();
            //System.out.println("sqltxt："+sqltxt);




            WktUtil wktUtil=new WktUtil();
            System.out.println(wktUtil.jsonToWkt(sqltxt));
        } catch (Exception e) {
            e.printStackTrace();
        }

    }

